close all;% load imageimg=imread('k1.png');figure;imshow(img);% gen noisenoise_sigma = 40;noise = randn(size(img)) .* noise_sigma;img_noiseadded = img + noise;figure;imshow(img_noiseadded);% create blur filterpkg load image;filter_size = 31;filter_sigma = 5;blur_kernel = fspecial('gaussian', filter_size, filter_sigma);% apply filterimg_noiseblurred = imfilter(img_noiseadded, blur_kernel);figure;imshow(img_noiseblurred);