if isoctave()     pkg load image;else     import image;endclose all;[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% load imageimg=imread(fullfile(path, 'k1.png'));figure;imshow(img);% gen noisenoise_sigma = 40;noise = uint8(randn(size(img)) .* noise_sigma);img_noiseadded = img + noise;figure;imshow(img_noiseadded);% create blur filterfilter_size = 31;filter_sigma = 5;blur_kernel = fspecial('gaussian', filter_size, filter_sigma);% apply filterimg_noiseblurred = imfilter(img_noiseadded, blur_kernel);figure;imshow(img_noiseblurred);