% compute the cross correlation between an image and a templateif isoctave()     pkg load image;else     import image;endclose all;disp('Create a random image of size 600x400');% create noise image with dimension (#rows, #cols) = (height, width)figure;I = uint8(randi(255, 400, 600));imshow(I);% clip templatefigure;T = I(51:100, 101:150);T = imnoise(T, 'gaussian');imshow(T);% perform normalized cross-correlationfigure;NCC = normxcorr2(T, I);surf(NCC); shading flat;% pause for few secondspause(4);close all;% load image[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% Load the imageI = rgb2gray(imread(fullfile(path, 'peppers.png')));T = rgb2gray(imread(fullfile(path, 'brown_pepper.png')));NCC = normxcorr2(T, I);figure;surf(NCC); shading flat;