if isoctave()   pkg load image; else   import image;endclose all;[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% load imageI = imread(fullfile(path, 'butterfly.jpg'));figure;imshow(I);title('original image');% apply gaussian filter with increasing sigmafsize = 31;figure;for fsigma=1:10  disp('================================');  disp('selected sigma = '); disp(fsigma);  h = fspecial('gaussian', fsize, fsigma);  O = imfilter(I, h, 'symmetric');  imshow(O);  title(sprintf('gaussian filter with sigma = %d', fsigma));  pause(2);end