if isoctave()   pkg load image; else   import image;endclear all;close all;[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% load imageI = double(rgb2gray(imread(fullfile(path, 'pentagon.png')))) / 255.;% figure; imshow(I);% sobel filter[gx, gy] = imgradientxy(I, 'sobel');[gmag, gdir] = imgradient(gx, gy);%imshow(gmag);figure;imshow(gmag / 4 * sqrt(2));figure;imshow((gdir + 180.) / 360.);