%% Using Hough Transform to find lines in an imageif isoctave()   pkg load image; else   import image;endclose all;[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% load imageI = imread(fullfile(path, 'cubism.png'));figure; imshow(I); title('original');% convert to grayscaleG = rgb2gray(I);figure; imshow(G); title('gray');% extract edgesE = edge(G, 'canny');figure; imshow(E); title('edge');% find all lines in the edge imagetheta = -90:90[accum rho] = houghtf(E, 'line', theta);figure; imagesc(accum, 'XData', theta, 'YData', rho); title('HoughTF');