if isoctave()   pkg load image; else   import image;endclear all;close all;[path, name, ext] = fileparts(mfilename('fullpath'));path = fullfile(path, '/../data/');str = sprintf('FULLPATH to data: [%s]', path);disp(str);% load imageI = rgb2gray(imread(fullfile(path, 'butterfly.png')));figure; imshow(I);% sobel filterK = fspecial('sobel');E = imfilter(double(I), K, 0);figure; imagesc(E); colormap gray;